import { useRouter } from 'next/router';
import Layout from '../../components/Layout';

const Content = props => (
    <>
        <h1>{props.show.name}</h1>
        <p>{props.show.summary.replace(/<[/]?[pb]>/g, '')}</p>
        {props.show.image ? <img src={props.show.image.medium} /> : null}
        </>
);

const Post = props => (
    <Layout>
        <Content  />
    </Layout>
);

export default Post;
